type IconName = keyof typeof Icons;

type custumIconProps = {
  name: IconName,
  size?: number

} & IconProps;

export const CustomIcon = ({ name, size, ...props }: custumIconProps) => {
  const Icon = Icons[name];
  return <Icon height={size} width={size} {...props} />;
}
